import React from 'react'
import { withStyles } from "@material-ui/core/styles";
import { Button, TextField, Card, CardContent } from "@material-ui/core";

const Email = () => {
    const InputField = withStyles({
        root: {
          '& label.Mui-focused': {
            color: '#7C3AED',
          },
          '& .MuiOutlinedInput-root': {
            '& .MuiOutlinedInput-input':{
              color: 'white',
            },
            '& fieldset': {
              borderColor: '#9CA3AF',
            },
            '&:hover fieldset': {
              borderColor: 'white',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#7C3AED',
            },

          },
          '& .MuiInputLabel-root': {
            color: 'white',
          }

        },
      })(TextField);

      const CustomCard = withStyles({
          root: {
              backgroundColor: '#242424',
              height: '100%',
              borderRadius: '15 px'
          }
      })(Card);

    return (
        <div class="h-full mx-auto p-5 grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2">
            <div class="flex-none h-full">
                <CustomCard>
                    <CardContent>
                        <form noValidate autoComplete="off">
                            <div class="grid grid-cols-1">
                                <div class="mt-3 px-2">
                                    <form noValidate autoComplete="off">
                                    <InputField label="Email Subject" variant="outlined" fullWidth="true"></InputField>
                                    </form>
                                </div>
                            </div>

                            <div class="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2">
                                <div class="mt-3 px-2">
                                    <InputField label="Email BCC" variant="outlined" fullWidth="true"></InputField>
                                </div>
                                <div class="mt-3 px-2">
                                    <InputField label="Email CC" variant="outlined" fullWidth="true"></InputField>
                                </div>  
                            </div>
                        </form>

                    </CardContent>
                </CustomCard>
            </div>
            <div class="flex-none">

            </div>
        </div>
    )
}

export default Email
