import React from "react";
import { Title, LoginTextField, LoginButton } from "./Materials";
import { Link } from "react-router-dom";
import { EmailService } from "../../services/EmailService";
import UserProfile from "../../contexts/UserProfile";

const Home = () => {
  const [profile, setProfile] = useContext(UserProfile);
  const username = React.createRef();
  const password = React.createRef();

  function handleClick(){
    const emailService = new EmailService();
    emailService.authenticateUser();
    setProfile()
  }
  return (
    <div class="h-full">
      <form
        class="h-full flex flex-wrap content-center justify-center flex-col"
        noValidate
        autoComplete="off"
      >
        <div class="text-center">
          <Title variant="h2">
            AutoSend
          </Title>
        </div>
        <div class="w-96 mt-5">
          <LoginTextField label="Email" variant="outlined" fullWidth="true" />
        </div>
        <div class="w-96 mt-3">
          <LoginTextField
            ref={passwordRe}
            type="password"
            label="Password"
            variant="outlined"
            fullWidth="true"
          />
        </div>

        <div class="w-96 mt-3 text-right">
          <Link to="/email">
            <LoginButton onClick={handleClick.bind()} variant="contained">Login</LoginButton>
          </Link>
        </div>

      </form>
    </div>
  );
};

export default Home;
