import React from "react";
import { Title, LoginTextField, LoginButton } from "./Materials";
import { Link } from "react-router-dom";
import { Typography } from "@material-ui/core";
import { config } from "../../config/Config";
import { PublicClientApplication } from '@azure/msal-browser';

const Home = () => {
  publicClientApplication = new PublicClientApplication({
    auth: {
        clientid: config.appId,
        redirectUri: config.redirectUri,
    },
    cache: {
        cacheLocation: 'sessionStorage',
        storeAuthStateInCookie: true
    }
});


async function login() {
    try{
        await this.publicClientApplication.loginPopup({
            scope: config.scopes,
            prompt: "select_account",
        });
        await getUserProfile();
    }
    catch(err){
        console.log(err)
    }
}

async function getUserProfile(){
  try{
    var accessToken = await getAccessToken(config.scopes);

  }
  catch(err){
    console.log(err);
  }
}

async function getAccessToken(scopes){
  try{
    const accounts = this.publicClientApplication.getAllAccounts();
    if (accounts.length <= 0) throw new Error('login_required');

    var silentResult = await this.publicClientApplication.acquireTokenSilent({
      scopes: scopes,
      account: accounts[0]
    });
    return silentResult.accessToken;
  }
  catch(err){
    console.log(err);
  }
}

  return (
    <div class="h-full">
      <form
        class="h-full flex flex-wrap content-center justify-center flex-col"
        noValidate
        autoComplete="off"
      >
        <div class="text-center">
          <Title variant="h2" colorPrimary="white">
            AutoSend
          </Title>
        </div>
        <div class="w-96 mt-5">
          <LoginTextField label="Email" variant="outlined" fullWidth="true" />
        </div>
        <div class="w-96 mt-3">
          <LoginTextField
            type="password"
            label="Password"
            variant="outlined"
            fullWidth="true"
          />
        </div>
        <div class="w-96 mt-3 text-right">
          {/* <Link to="/email"> */}
            <LoginButton onClick={login()} variant="contained">Login</LoginButton>
          {/* </Link> */}
        </div>
      </form>
    </div>
  );
};

export default Home;
