import React, { useContext, useEffect }  from "react";
import { CardContent } from "@material-ui/core";
import { InputField, CustomCard, ActionButton, Text, EmailPreview } from "./Materials";
import Information from "../information/Information";
import EmailService from "../../services/EmailService";
import UserProfile from "../../contexts/UserProfile";

const Email = () => {
  const [profile, setProfile] = useContext(UserProfile);
  const hiddenFileRef = React.createRef();
  const subjectRef = React.createRef();
  const ccRef = React.createRef();
  const bccRef = React.createRef();
  const recipientsRef = React.createRef();
  const namesRef = React.createRef();
  const positionsRef = React.createRef();
  const messageRef = React.createRef();
  const attachmentRef = React.createRef();
  const previewRef = React.createRef();


  let emailContent = {};

  function handleClick(){
    emailContent['email'] = profile.email;
    emailContent['password'] = profile.password;
    emailContent['subject'] = subjectRef.current.children[1].children[0].value;
    emailContent['cc'] = ccRef.current.children[1].children[0].value;
    emailContent['bcc'] = bccRef.current.children[1].children[0].value;
    emailContent['recipients'] = recipientsRef.current.children[1].children[0].value;
    emailContent['names'] = namesRef.current.children[1].children[0].value;
    emailContent['positions'] = positionsRef.current.children[1].children[0].value;
    emailContent['message'] = messageRef.current.children[1].children[0].value;

    let fileNames = []
    for(let i = 0; i < hiddenFileRef.current.files.length; i++){
      fileNames.push(hiddenFileRef.current.files[i].name);
    }

    emailContent['attachments'] = fileNames;
    EmailService.sendEmail(JSON.stringify(emailContent));
  }

  function handleChange(){
    let name = namesRef.current.children[1].children[0].value.split(",")[0];
    let position = positionsRef.current.children[1].children[0].value.split(",")[0];
    let message = messageRef.current.children[1].children[0].value.replace("{name}", name)
                                                                  .replace("{position}", position)
    previewRef.current.textContent = message;
  }

  function getFiles(){
    if(hiddenFileRef.current.files.length === 1){
      attachmentRef.current.children[0].children[0].value = hiddenFileRef.current.files[0].name;
    }
    else{
      attachmentRef.current.children[0].children[0].value = String(hiddenFileRef.current.files.length) + " files selected";
    }

  }

  return (
    <div>
      <div class="p-5 mt-3">
        <Information/>
      </div>
      <div class="flex content-center p-5 grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2">
        <div class="flex-none h-full px-2 py-2">
          <CustomCard>
            <CardContent>
              <div class="px-2">
                <Text variant="h6">Email Information</Text>
              </div>
              <div class="grid grid-cols-1">
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      required
                      ref={subjectRef}
                      label="Subject"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid sm:grid-cols-1 md:grid-cols-2 lg:grid-cols-2">
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      ref={bccRef}
                      label="BCC"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      ref={ccRef}
                      label="CC"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid grid-cols-1">
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      required
                      ref={recipientsRef}
                      label="Recipients"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid grid-cols-1">
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      ref={namesRef}
                      label="Names"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid grid-cols-1">
                <form noValidate autoComplete="off">
                  <div class="mt-6 px-2">
                    <InputField
                      ref={positionsRef}
                      label="Positions"
                      variant="outlined"
                      fullWidth={true}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid grid-cols-1">
                <form noValidate autoComplete="off">
                  <div class="mt-5 px-2">
                    <InputField
                      required
                      ref={messageRef}
                      onChange={handleChange.bind(this)}
                      label="Message"
                      variant="outlined"
                      fullWidth={true}
                      multiline
                      rows={10}
                    ></InputField>
                  </div>
                </form>
              </div>

              <div class="grid grid-cols-1 px-2 mt-6">
                <ActionButton onClick={handleClick.bind(this)}>Send Messages</ActionButton>
              </div>
            </CardContent>
          </CustomCard>
        </div>

        <div class="flex-none h-full px-2 sm:grid-cols-1 py-2">
          <CustomCard>
            <EmailPreview style={{whiteSpace: 'pre-line'}}>
              <div class="px-2">
                <Text variant="h6">Email Preview</Text>
              </div>
              <div class="h-full flex flex-wrap content-center justify-center">
                <Text ref={previewRef}>Type a message to see me update</Text>
              </div>
            </EmailPreview>
          </CustomCard>
        </div>
      </div>
    </div>
  );
};

export default Email;
